
public class LearningOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		        // Addition operation
		
				int num1 = 20, num2 = 35;
				int num3 = num2 + num1;
				System.out.println("Addition of two numbers: "+num3);
				
				//Subtraction operation
				
				num3 = num1 - num2;
				System.out.println("Difference of two nmbers: "+num3);
				
				//Multiplication operation
				
				num3 = num2*num1;
				System.out.println("Product of two numbers: "+num3);
				
				//Division operation - outputs only Quotient value
				
				num3 = num2/num1;
				System.out.println("Division of two numbers: "+num3);
				
				//Modulus operation - working with the reminder
				
				num3 = num2%num1;
				System.out.println("Modulus of two numbers: "+num3);
				}

		

}


