
public class PlusEqualTo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Plus equal to '+='
		
		int num1 = 6, num2 = 5;
		
		num1 += 4;  // num1 = num1 + 4;
		
		System.out.println(num1);
		
		//Minus equal to  '-='
		
		num1 -= 3; // num1 = num1 - 3;
		System.out.println(num1);
		
		//multiply equalto '*='
		
		num1 *= num2; //  num1 = num1 * num2;
		System.out.println(num1);
		
		//division equal to '/='
		
		num1 /= num2; // num1 = num1/num2;
		
		System.out.println(num1);
		
		
		//Modulus equal to '%='
		
		num1 %= num2; // num1 = num1 % num2
		System.out.println(num1);
		
	}

}
