
public class switchCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String month = "month";
		switch(month) {
		case "January":
			System.out.println("Month of the year : January");
			break;
		case "Feburary":
			System.out.println("Month of the year : Feburary");
			break;			
		case "March":
			System.out.println("Month of the year : March");
			break;
		case "April":
			System.out.println("Month of the year : April");
			break;
		case "May":
			System.out.println("Month of the year : May");
			break;
		case "June":
			System.out.println("Month of the year : June");
			break;
		case "July":
			System.out.println("Month of the year : July");
			break;
		case "August":
			System.out.println("Month of the year : August");
			break;
		case "September":
			System.out.println("Month of the year : Septembery");
			break;
		case "October":
			System.out.println("Month of the year : October");
			break;
		case "November":
			System.out.println("Month of the year : November");
			break;
		case "December":
			System.out.println("Month of the year : December");
			break;
		default:
			System.out.println("Invalid input");
			break;
			
		}

	}

}
